import React, { Component } from 'react'

export class AddTodos extends Component {
    state =
    {
        title:''
    }
    render() {
    return (
      <form style={{display: 'flex'}}>
          <input
          type="text"
          name="title"
          style={{flex: '10', padding:'5px'}}
          placeholder="Add Todo ..."
          value={this.state.title}
          />
            <input
            type="submit"
            value="submit"
            className="btn"
            style={{flex:1}}
            />

      </form>
    )
  }
}

export default AddTodos
